settings.code:
{% comment %}
  LaVivid Blog List Page - Custom Liquid Version
  Usage: Add Custom Liquid Section in Theme Editor, paste this code
  Modify variables below to customize content
{% endcomment %}

{% comment %} ===== Configurable Variables ===== {% endcomment %}
{% assign hero_label = "LaVivid Blog" %}
{% assign hero_title_1 = "Real Stories." %}
{% assign hero_title_2 = "Real Confidence" %}
{% assign hero_subtitle = "Your Journey to a Full Head Hair Starts Here" %}
{% assign cta_text = "Get $40 OFF Your First System" %}
{% assign cta_link = "/collections/men-hair-system" %}
{% assign featured_title = "New to Hair Systems? Start Here" %}
{% assign articles_per_page = 5 %}

{% comment %} Beginner Guide Links {% endcomment %}
{% assign beginner_link_1 = "/pages/hair-systems-for-beginners" %}
{% assign beginner_title_1 = "Hair Systems for Beginners" %}
{% assign beginner_desc_1 = "Everything you need to know to get started with your first hair system." %}
{% assign beginner_link_2 = "/pages/faq-mens-hair-replacement-systems" %}
{% assign beginner_title_2 = "FAQ: Men's Hair Replacement" %}
{% assign beginner_desc_2 = "Answers to the most common questions about hair systems." %}

{% comment %} 
  ===== Blog Handles Configuration =====
  Ordered by men's hair system business priority:
  1. Beginner tutorials (101 series)
  2. Core product knowledge (hair-system, hair-replacement)
  3. Care guides (adhesives)
  4. Customer stories
  5. New products/news
{% endcomment %}
{% assign blog_handles = "news,hair-system,skin-hair-pieces-101,lace-hair-pieces-101,men-s-hair-loss,hair-replacement-for-men,hair-system-adhesives,what-our-customer-say1,new-products,hair-pieces,hair-replacement,blog,hair-system-ebook" | split: "," %}

{% comment %} Get current blog handle {% endcomment %}
{% assign current_blog_handle = request.path | split: '/' | last | default: 'all' %}
{% if current_blog_handle == 'blogs' or current_blog_handle == '' %}
  {% assign current_blog_handle = 'all' %}
{% endif %}

<style>
  /* ===== CSS Variables ===== */
  .blog-list-wrapper {
    --bl-brand: #68699a;
    --bl-brand-light: rgba(104, 105, 154, 0.1);
    --bl-text: rgba(var(--color-foreground), 1);
    --bl-bg: rgba(var(--color-background), 1);
    --bl-border: rgba(var(--color-foreground), 0.12);
    --bl-hover: rgba(var(--color-foreground), 0.04);
    --bl-dark: #1a1a2e;
    --bl-dark-text: #ffffff;

    font-family: var(--font-body-family);
    color: var(--bl-text);
    line-height: 1.6;
  }

  .bl-container {
    max-width: 900px;
    margin: 0 auto;
    padding: 0 20px;
  }

  /* ===== HERO Section (Compact) ===== */
  .bl-hero {
    background: linear-gradient(135deg, #f8f9fa 0%, #e9ecef 100%);
    padding: 20px;
    border-bottom: 1px solid var(--bl-border);
  }
  .bl-hero-content {
    display: flex;
    align-items: center;
    justify-content: space-between;
    gap: 20px;
    max-width: 900px;
    margin: 0 auto;
    flex-wrap: wrap;
  }
  .bl-hero-text {
    display: flex;
    align-items: center;
    gap: 16px;
    flex-wrap: wrap;
  }
  .bl-hero h1 {
    color: var(--bl-text);
    margin: 0;
    font-size: 1.3rem;
    font-weight: 600;
    line-height: 1.3;
  }
  .bl-hero h1 span {
    color: var(--bl-brand);
  }

  /* CTA Button */
  .bl-btn {
    display: inline-block;
    background: var(--bl-brand);
    color: #fff;
    padding: 10px 20px;
    font-weight: 600;
    font-size: 0.85rem;
    text-decoration: none;
    border-radius: 6px;
    transition: all 0.2s;
    white-space: nowrap;
  }
  .bl-btn:hover {
    filter: brightness(1.1);
    color: #fff;
  }

  /* ===== FEATURED Section ===== */
  .bl-featured {
    padding: 50px 20px;
    background: var(--bl-bg);
  }
  .bl-featured-title {
    font-size: 1.4rem;
    font-weight: 600;
    margin-bottom: 24px;
    text-align: center;
  }
  .bl-featured-grid {
    display: grid;
    grid-template-columns: 1fr 1fr;
    gap: 16px;
    max-width: 800px;
    margin: 0 auto;
  }
  .bl-featured-card {
    background: #fff;
    border: 1px solid var(--bl-border);
    border-radius: 12px;
    padding: 20px;
    display: flex;
    gap: 16px;
    transition: all 0.2s;
    text-decoration: none;
    color: inherit;
    box-shadow: 0 2px 8px rgba(0,0,0,0.04);
  }
  .bl-featured-card:hover {
    border-color: var(--bl-brand);
    box-shadow: 0 8px 24px rgba(104, 105, 154, 0.18);
    transform: translateY(-2px);
  }
  .bl-featured-images {
    display: flex;
    gap: 8px;
    flex-shrink: 0;
  }
  .bl-featured-images img {
    width: 60px;
    height: 60px;
    object-fit: cover;
    border-radius: 8px;
  }
  .bl-featured-info h3 {
    font-size: 1rem;
    font-weight: 600;
    margin: 0 0 8px 0;
    line-height: 1.3;
  }
  .bl-featured-info p {
    font-size: 0.85rem;
    color: var(--bl-text);
    opacity: 0.65;
    margin: 0;
    line-height: 1.5;
  }

  .bl-filter-section {
    padding: 30px 20px;
    background: var(--bl-bg);
    position: relative;
  }
  .bl-filter-bar {
    display: flex;
    justify-content: center;
    gap: 10px;
    flex-wrap: wrap;
    max-width: 900px;
    margin: 0 auto;
  }
  .bl-filter-btn {
    padding: 10px 20px;
    border: 1px solid var(--bl-border);
    background: transparent;
    color: var(--bl-text);
    font-size: 0.9rem;
    border-radius: 25px;
    cursor: pointer;
    transition: all 0.2s ease;
    text-decoration: none;
    white-space: nowrap;
  }
  .bl-filter-btn:hover {
    border-color: var(--bl-brand);
    color: var(--bl-brand);
  }
  .bl-filter-btn.active {
    background: transparent;
    border-color: var(--bl-brand);
    color: var(--bl-brand);
    font-weight: 600;
  }
  .bl-filter-btn.active::before {
    content: '[';
    margin-right: 2px;
  }
  .bl-filter-btn.active::after {
    content: ']';
    margin-left: 2px;
  }
  .bl-hidden-filter {
    display: none;
  }
  .bl-filters-expanded .bl-hidden-filter {
    display: inline-flex;
  }
  .bl-more-btn {
    background: var(--bl-brand-light);
    border-color: transparent;
  }
  .bl-more-btn:hover {
    background: var(--bl-brand);
    color: #fff;
    border-color: var(--bl-brand);
  }

  /* ===== ARTICLE LIST Section ===== */
  .bl-articles {
    padding: 40px 20px 60px;
    background: var(--bl-bg);
  }
  .bl-article-list {
    display: flex;
    flex-direction: column;
    gap: 20px;
    max-width: 900px;
    margin: 0 auto;
  }
  .bl-article-card {
    background: #fff;
    border: 1px solid var(--bl-border);
    border-radius: 12px;
    display: flex;
    gap: 20px;
    padding: 16px;
    transition: all 0.2s;
    text-decoration: none;
    color: inherit;
  }
  .bl-article-card:hover {
    border-color: var(--bl-brand);
    box-shadow: 0 4px 20px rgba(104, 105, 154, 0.12);
  }
  .bl-article-image {
    flex-shrink: 0;
    width: 120px;
    height: 120px;
    border-radius: 8px;
    overflow: hidden;
    background: var(--bl-brand-light);
  }
  .bl-article-image img {
    width: 100%;
    height: 100%;
    object-fit: cover;
  }
  .bl-article-image svg {
    width: 50%;
    height: 50%;
    margin: 25%;
    color: var(--bl-brand);
    opacity: 0.4;
  }
  .bl-article-content {
    flex: 1;
    display: flex;
    flex-direction: column;
    justify-content: center;
  }
  .bl-article-content h3 {
    font-size: 1.1rem;
    font-weight: 600;
    margin: 0 0 8px 0;
    line-height: 1.3;
    color: var(--bl-text);
  }
  .bl-article-content p {
    font-size: 0.9rem;
    color: var(--bl-text);
    opacity: 0.65;
    margin: 0;
    line-height: 1.5;
    display: -webkit-box;
    -webkit-line-clamp: 2;
    -webkit-box-orient: vertical;
    overflow: hidden;
  }
  .bl-article-meta {
    margin-top: 10px;
    font-size: 0.8rem;
    color: var(--bl-brand);
  }

  /* ===== PAGINATION ===== */
  .bl-pagination {
    display: flex;
    justify-content: center;
    gap: 8px;
    margin-top: 40px;
    flex-wrap: wrap;
  }
  .bl-pagination a,
  .bl-pagination span {
    display: inline-flex;
    align-items: center;
    justify-content: center;
    min-width: 40px;
    height: 40px;
    padding: 0 12px;
    border: 1px solid var(--bl-border);
    border-radius: 8px;
    font-size: 0.9rem;
    text-decoration: none;
    color: var(--bl-text);
    transition: all 0.2s;
  }
  .bl-pagination a:hover {
    border-color: var(--bl-brand);
    color: var(--bl-brand);
  }
  .bl-pagination .current {
    background: var(--bl-brand);
    border-color: var(--bl-brand);
    color: #fff;
  }

  /* ===== EMPTY STATE ===== */
  .bl-empty {
    text-align: center;
    padding: 60px 20px;
    opacity: 0.6;
  }

  /* ===== Responsive ===== */
  @media (max-width: 768px) {
    .bl-hero { padding: 40px 20px; }
    .bl-hero-content {
      flex-direction: column;
      text-align: center;
    }
    .bl-hero-image {
      width: 200px;
      height: 200px;
      order: -1;
    }
    .bl-featured-grid {
      grid-template-columns: 1fr;
    }
    .bl-featured-card {
      flex-direction: column;
    }
    .bl-article-card {
      flex-direction: row;
    }
    .bl-article-image {
      width: 100px;
      height: 100px;
    }
    .bl-filter-section {
      position: relative;
    }
    .bl-filter-section::after {
      content: '';
      position: absolute;
      right: 0;
      top: 0;
      bottom: 0;
      width: 40px;
      background: linear-gradient(to right, transparent, var(--bl-bg));
      pointer-events: none;
    }
    .bl-filter-bar {
      justify-content: flex-start;
      overflow-x: auto;
      flex-wrap: nowrap;
      padding-bottom: 8px;
      padding-right: 30px;
      -webkit-overflow-scrolling: touch;
      scrollbar-width: none;
      -ms-overflow-style: none;
    }
    .bl-filter-bar::-webkit-scrollbar {
      display: none;
    }
    .bl-filter-btn {
      padding: 8px 16px;
      font-size: 0.85rem;
    }
    .bl-hidden-filter {
      display: inline-flex;
    }
    .bl-more-btn {
      display: none;
    }
  }
</style>

<div class="blog-list-wrapper">

  <!-- HERO (Compact Bar) -->
  <section class="bl-hero">
    <div class="bl-hero-content">
      <div class="bl-hero-text">
        <h1>{{ hero_title_1 }} <span>{{ hero_title_2 }}</span></h1>
      </div>
      <a href="{{ cta_link }}" class="bl-btn">{{ cta_text }}</a>
    </div>
  </section>

  <!-- FEATURED SECTION - Beginner Guide Entry -->
  <section class="bl-featured">
    <h2 class="bl-featured-title">{{ featured_title }}</h2>
    <div class="bl-featured-grid">
      <!-- Beginner Landing Page -->
      <a href="{{ beginner_link_1 }}" class="bl-featured-card">
        <div class="bl-featured-images">
          <svg viewBox="0 0 24 24" fill="none" stroke="currentColor" stroke-width="1.5" style="width:60px;height:60px;color:var(--bl-brand);">
            <path d="M12 6.042A8.967 8.967 0 0 0 6 3.75c-1.052 0-2.062.18-3 .512v14.25A8.987 8.987 0 0 1 6 18c2.305 0 4.408.867 6 2.292m0-14.25a8.966 8.966 0 0 1 6-2.292c1.052 0 2.062.18 3 .512v14.25A8.987 8.987 0 0 0 18 18a8.967 8.967 0 0 0-6 2.292m0-14.25v14.25"/>
          </svg>
        </div>
        <div class="bl-featured-info">
          <h3>{{ beginner_title_1 }}</h3>
          <p>{{ beginner_desc_1 }}</p>
        </div>
      </a>
      <!-- FAQ Page -->
      <a href="{{ beginner_link_2 }}" class="bl-featured-card">
        <div class="bl-featured-images">
          <svg viewBox="0 0 24 24" fill="none" stroke="currentColor" stroke-width="1.5" style="width:60px;height:60px;color:var(--bl-brand);">
            <path d="M9.879 7.519c1.171-1.025 3.071-1.025 4.242 0 1.172 1.025 1.172 2.687 0 3.712-.203.179-.43.326-.67.442-.745.361-1.45.999-1.45 1.827v.75M21 12a9 9 0 1 1-18 0 9 9 0 0 1 18 0Zm-9 5.25h.008v.008H12v-.008Z"/>
          </svg>
        </div>
        <div class="bl-featured-info">
          <h3>{{ beginner_title_2 }}</h3>
          <p>{{ beginner_desc_2 }}</p>
        </div>
      </a>
    </div>
  </section>

  <!-- CATEGORY FILTER -->
  <section class="bl-filter-section">
    <div class="bl-filter-bar" id="bl-filters">
      <button class="bl-filter-btn active" data-blog="all">All</button>
      {% assign filter_index = 0 %}
      {% for handle in blog_handles %}
        {% assign current_blog = blogs[handle] %}
        {% if current_blog %}
          <button class="bl-filter-btn{% if filter_index >= 7 %} bl-hidden-filter{% endif %}" data-blog="{{ current_blog.handle }}" data-index="{{ filter_index }}">{{ current_blog.title }}</button>
          {% assign filter_index = filter_index | plus: 1 %}
        {% endif %}
      {% endfor %}
      {% if filter_index > 7 %}
        <button class="bl-filter-btn bl-more-btn" id="bl-toggle-filters">More +</button>
      {% endif %}
    </div>
  </section>

  <!-- ARTICLE LIST (sorted by date + pagination) -->
  <section class="bl-articles">
    <div class="bl-article-list" id="bl-article-list">
      {% for handle in blog_handles %}
        {% assign current_blog = blogs[handle] %}
        {% if current_blog %}
          {% for article in current_blog.articles %}
            <a href="{{ article.url }}" class="bl-article-card" 
               data-blog="{{ current_blog.handle }}" 
               data-date="{{ article.published_at | date: '%Y%m%d%H%M%S' }}"
               style="display: none;">
              <div class="bl-article-image">
                {% if article.image != blank %}
                  {{ article.image | image_url: width: 240 | image_tag: loading: 'lazy' }}
                {% else %}
                  <svg viewBox="0 0 24 24" fill="none" stroke="currentColor" stroke-width="1">
                    <rect x="3" y="3" width="18" height="18" rx="2"/>
                    <circle cx="8.5" cy="8.5" r="1.5"/>
                    <path d="m21 15-5-5L5 21"/>
                  </svg>
                {% endif %}
              </div>
              <div class="bl-article-content">
                <h3>{{ article.title }}</h3>
                <p>{{ article.excerpt_or_content | strip_html | truncate: 150 }}</p>
                <span class="bl-article-meta">{{ article.published_at | date: '%B %d, %Y' }} · {{ current_blog.title }}</span>
              </div>
            </a>
          {% endfor %}
        {% endif %}
      {% endfor %}
    </div>

    <!-- Pagination -->
    <div class="bl-pagination" id="bl-pagination"></div>

    <div id="bl-empty" class="bl-empty" style="display: none;">
      <p>No articles found.</p>
    </div>
  </section>

</div>

<script>
(function() {
  const ITEMS_PER_PAGE = {{ articles_per_page }};
  const articleList = document.getElementById('bl-article-list');
  const paginationContainer = document.getElementById('bl-pagination');
  const emptyMsg = document.getElementById('bl-empty');
  const filterBtns = document.querySelectorAll('.bl-filter-btn:not(.bl-more-btn)');
  const filterBar = document.getElementById('bl-filters');
  const toggleBtn = document.getElementById('bl-toggle-filters');

  // Toggle More/Less filters
  if (toggleBtn) {
    toggleBtn.addEventListener('click', function() {
      filterBar.classList.toggle('bl-filters-expanded');
      this.textContent = filterBar.classList.contains('bl-filters-expanded') ? 'Less −' : 'More +';
    });
  }

  // Get all articles as array
  let allArticles = Array.from(document.querySelectorAll('.bl-article-card'));

  // Sort by date (newest first)
  allArticles.sort((a, b) => {
    const dateA = a.getAttribute('data-date') || '0';
    const dateB = b.getAttribute('data-date') || '0';
    return dateB.localeCompare(dateA);
  });

  // Re-insert sorted articles
  allArticles.forEach(article => articleList.appendChild(article));

  let currentFilter = 'all';
  let currentPage = 1;

  // Get filtered articles
  function getFilteredArticles() {
    return allArticles.filter(article => {
      if (currentFilter === 'all') return true;
      return article.getAttribute('data-blog') === currentFilter;
    });
  }

  // Render pagination
  function renderPagination(totalItems) {
    const totalPages = Math.ceil(totalItems / ITEMS_PER_PAGE);
    paginationContainer.innerHTML = '';

    if (totalPages <= 1) return;

    // Previous page
    if (currentPage > 1) {
      const prevBtn = document.createElement('a');
      prevBtn.href = '#';
      prevBtn.textContent = '‹ Prev';
      prevBtn.addEventListener('click', (e) => {
        e.preventDefault();
        currentPage--;
        render();
        scrollToArticles();
      });
      paginationContainer.appendChild(prevBtn);
    }

    // Page numbers
    for (let i = 1; i <= totalPages; i++) {
      if (totalPages > 7) {
        // Ellipsis logic
        if (i === 1 || i === totalPages || (i >= currentPage - 1 && i <= currentPage + 1)) {
          const pageBtn = createPageBtn(i);
          paginationContainer.appendChild(pageBtn);
        } else if (i === currentPage - 2 || i === currentPage + 2) {
          const dots = document.createElement('span');
          dots.textContent = '...';
          paginationContainer.appendChild(dots);
        }
      } else {
        const pageBtn = createPageBtn(i);
        paginationContainer.appendChild(pageBtn);
      }
    }

    // Next page
    if (currentPage < totalPages) {
      const nextBtn = document.createElement('a');
      nextBtn.href = '#';
      nextBtn.textContent = 'Next ›';
      nextBtn.addEventListener('click', (e) => {
        e.preventDefault();
        currentPage++;
        render();
        scrollToArticles();
      });
      paginationContainer.appendChild(nextBtn);
    }
  }

  function createPageBtn(pageNum) {
    const btn = pageNum === currentPage 
      ? document.createElement('span') 
      : document.createElement('a');

    if (pageNum !== currentPage) {
      btn.href = '#';
      btn.addEventListener('click', (e) => {
        e.preventDefault();
        currentPage = pageNum;
        render();
        scrollToArticles();
      });
    } else {
      btn.classList.add('current');
    }
    btn.textContent = pageNum;
    return btn;
  }

  function scrollToArticles() {
    document.querySelector('.bl-filter-section').scrollIntoView({ behavior: 'smooth' });
  }

  // Main render function
  function render() {
    const filtered = getFilteredArticles();
    const startIndex = (currentPage - 1) * ITEMS_PER_PAGE;
    const endIndex = startIndex + ITEMS_PER_PAGE;

    // Hide all articles
    allArticles.forEach(article => article.style.display = 'none');

    // Show current page articles
    filtered.slice(startIndex, endIndex).forEach(article => {
      article.style.display = 'flex';
    });

    // Render pagination
    renderPagination(filtered.length);

    // Show/hide empty state
    emptyMsg.style.display = filtered.length === 0 ? 'block' : 'none';
  }

  // Filter button click
  filterBtns.forEach(btn => {
    btn.addEventListener('click', function() {
      filterBtns.forEach(b => b.classList.remove('active'));
      this.classList.add('active');
      currentFilter = this.getAttribute('data-blog');
      currentPage = 1; // Reset to first page
      render();
    });
  });

  // Initial render
  render();
})();
</script>
