{% comment %}
  LaVivid Shipping Page - Optimized Version
  Style: Native Theme Colors + Brand Purple (#68699a) + Dark Hero
  Features: Continent Filtering, Search, Mobile 2-Column, SVG Icons
{% endcomment %}

{% assign hero_title = "Worldwide Shipping Availability" %}
{% assign hero_sub = "LaVivid ships to a wide range of countries and regions worldwide. Use the list or search below to check whether we currently ship to your location." %}
{% assign us_free_threshold = 17900 %}
{% assign intl_free_threshold = 24900 %}
{% assign us_shipping_time = "5-7" %}
{% assign intl_shipping_time = "7-10" %}

<style>
  /* ===== CSS Variables ===== */
  .lp-wrapper {
    --lp-brand: #68699a;
    --lp-brand-light: rgba(104, 105, 154, 0.1);
    --lp-text: rgba(var(--color-foreground), 1);
    --lp-bg: rgba(var(--color-background), 1);
    --lp-border: rgba(var(--color-foreground), 0.12);
    --lp-hover: rgba(var(--color-foreground), 0.04);
    --lp-dark: #1a1a2e;
    --lp-dark-text: #ffffff;

    font-family: var(--font-body-family);
    color: var(--lp-text);
    line-height: 1.6;
  }

  .lp-container {
    max-width: 1200px;
    margin: 0 auto;
    padding: 0 20px;
  }

  /* ===== 1. HERO Section ===== */
  .lp-hero {
    background: linear-gradient(135deg, var(--lp-dark) 0%, #2d2d44 100%);
    color: var(--lp-dark-text);
    padding: 80px 20px;
    text-align: center;
  }
  .lp-hero h1 {
    color: #fff;
    margin: 0 0 16px 0;
    font-size: clamp(1.8rem, 5vw, 2.8rem);
    font-weight: 600;
    line-height: 1.2;
  }
  .lp-hero p {
    color: rgba(255,255,255, 0.75);
    max-width: 600px;
    margin: 0 auto;
    font-size: 1.1rem;
  }

  /* ===== 2. COUNTRY LIST Section ===== */
  .lp-list-section {
    padding: 60px 0;
    background-color: var(--lp-bg);
  }
  .lp-section-title {
    text-align: center;
    margin-bottom: 30px;
    font-size: 1.6rem;
    font-weight: 600;
  }

  /* Continent Filter Tabs */
  .lp-filter-bar {
    display: flex;
    justify-content: center;
    gap: 8px;
    flex-wrap: wrap;
    margin-bottom: 24px;
    padding: 0 10px;
  }
  .lp-filter-btn {
    padding: 8px 18px;
    border: 1px solid var(--lp-border);
    background: transparent;
    color: var(--lp-text);
    font-size: 0.9rem;
    border-radius: 20px;
    cursor: pointer;
    transition: all 0.2s ease;
    white-space: nowrap;
  }
  .lp-filter-btn:hover {
    border-color: var(--lp-brand);
    color: var(--lp-brand);
  }
  .lp-filter-btn.active {
    background: var(--lp-brand);
    border-color: var(--lp-brand);
    color: #fff;
  }

  /* Search Box - Modern design with icon inside */
  .lp-search-wrap {
    display: flex;
    align-items: center;
    gap: 10px;
    max-width: 500px;
    margin: 0 auto 32px;
    padding: 0 16px;
    border: 1px solid var(--lp-border);
    border-radius: 8px;
    background: var(--lp-bg);
    transition: all 0.2s ease;
  }
  .lp-search-wrap:hover {
    border-color: rgba(var(--color-foreground), 0.25);
  }
  .lp-search-wrap:focus-within {
    border-color: var(--lp-brand);
    box-shadow: 0 0 0 3px var(--lp-brand-light);
  }
  .lp-search-icon {
    flex-shrink: 0;
    width: 20px;
    height: 20px;
    opacity: 0.45;
  }
  .lp-search-box {
    flex: 1;
    padding: 14px 0;
    background: transparent;
    border: none;
    color: var(--lp-text);
    font-size: 1rem;
    outline: none;
    min-width: 0;
  }
  .lp-search-box::placeholder { 
    opacity: 0.5; 
    color: var(--lp-text); 
  }

  /* Country Grid */
  .lp-flat-grid {
    display: grid;
    grid-template-columns: repeat(auto-fill, minmax(200px, 1fr));
    gap: 12px;
  }

  /* Country Card */
  .lp-item {
    display: flex;
    align-items: center;
    gap: 10px;
    padding: 12px 14px;
    border: 1px solid var(--lp-border);
    border-radius: 6px;
    transition: all 0.2s ease;
    font-size: 0.95rem;
    color: var(--lp-text);
    background: var(--lp-bg);
  }
  .lp-item:hover {
    border-color: var(--lp-brand);
    background: var(--lp-brand-light);
  }
  .lp-item img {
    width: 24px;
    height: auto;
    border-radius: 2px;
    flex-shrink: 0;
  }
  .lp-item span {
    overflow: hidden;
    text-overflow: ellipsis;
    white-space: nowrap;
  }

  .lp-hidden { display: none !important; }

  .lp-empty {
    text-align: center;
    padding: 40px 20px;
    opacity: 0.6;
    display: none;
  }

  /* Shipping Notes */
  .lp-notes {
    margin-top: 48px;
    padding-top: 32px;
    border-top: 1px solid var(--lp-border);
  }
  .lp-notes h3 {
    font-size: 1.2rem;
    font-weight: 600;
    margin: 0 0 16px 0;
  }
  .lp-notes p {
    font-size: 0.95rem;
    line-height: 1.7;
    color: var(--lp-text);
    opacity: 0.8;
    margin: 0 0 12px 0;
  }
  .lp-notes p:last-child {
    margin-bottom: 0;
  }
  .lp-notes-help {
    margin-top: 20px !important;
    padding: 16px 20px;
    background: rgba(var(--color-foreground), 0.03);
    border-radius: 8px;
    opacity: 1 !important;
  }
  .lp-notes a {
    color: var(--lp-brand);
    text-decoration: none;
    font-weight: 500;
  }
  .lp-notes a:hover {
    text-decoration: underline;
  }

  /* ===== 3. BENEFITS Section ===== */
  .lp-benefits {
    padding: 60px 0;
    background: rgba(var(--color-foreground), 0.02);
  }
  .lp-b-grid {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(280px, 1fr));
    gap: 24px;
  }
  .lp-b-card {
    background: var(--lp-bg);
    border: 1px solid var(--lp-border);
    padding: 32px 24px;
    border-radius: 10px;
    text-align: center;
    transition: border-color 0.2s;
  }
  .lp-b-card:hover {
    border-color: var(--lp-brand);
  }
  .lp-b-icon {
    width: 48px;
    height: 48px;
    margin: 0 auto 16px;
    color: var(--lp-brand);
  }
  .lp-b-card h3 {
    margin: 0 0 8px 0;
    font-size: 1.15rem;
  }
  .lp-b-card p {
    margin: 0;
    opacity: 0.7;
    font-size: 0.95rem;
  }

  /* ===== 4. RESTRICTIONS Section ===== */
  .lp-restrict {
    background: linear-gradient(135deg, var(--lp-dark) 0%, #2d2d44 100%);
    color: var(--lp-dark-text);
    padding: 60px 0;
  }
  .lp-restrict-title {
    text-align: center;
    color: #fff;
    margin-bottom: 40px;
    font-size: 1.5rem;
  }
  .lp-restrict-box {
    max-width: 800px;
    margin: 0 auto;
    border-left: 3px solid var(--lp-brand);
    padding-left: 24px;
  }
  .lp-restrict h4 {
    color: #fff;
    margin: 0 0 6px 0;
    font-size: 1.05rem;
  }
  .lp-restrict p {
    color: rgba(255,255,255,0.65);
    font-size: 0.95rem;
    margin: 0 0 24px 0;
  }
  .lp-restrict p:last-child { margin-bottom: 0; }

  /* ===== 5. TRUST & CTA Section ===== */
  .lp-trust {
    padding: 60px 0;
    text-align: center;
  }
  .lp-trust-icons {
    display: flex;
    justify-content: center;
    gap: 48px;
    flex-wrap: wrap;
    margin-bottom: 48px;
  }
  .lp-t-item {
    display: flex;
    flex-direction: column;
    align-items: center;
    gap: 10px;
  }
  .lp-t-icon {
    width: 36px;
    height: 36px;
    color: var(--lp-brand);
  }
  .lp-t-item span {
    font-weight: 500;
    font-size: 0.95rem;
  }

  .lp-trust h2 {
    margin: 0 0 24px 0;
    font-size: 1.6rem;
  }

  .lp-btn {
    display: inline-block;
    background: var(--lp-brand);
    color: #fff;
    padding: 14px 40px;
    font-weight: 600;
    font-size: 1rem;
    text-decoration: none;
    border-radius: 6px;
    transition: all 0.2s;
  }
  .lp-btn:hover {
    filter: brightness(1.1);
    color: #fff;
    transform: translateY(-1px);
  }

  /* ===== Responsive ===== */
  @media (max-width: 768px) {
    .lp-hero { padding: 60px 20px; }
    .lp-list-section, .lp-benefits, .lp-restrict, .lp-trust { padding: 48px 0; }

    .lp-flat-grid {
      grid-template-columns: repeat(2, 1fr);
      gap: 10px;
    }
    .lp-item {
      padding: 10px 12px;
      font-size: 0.85rem;
    }
    .lp-item img { width: 20px; }

    .lp-filter-bar {
      justify-content: flex-start;
      overflow-x: auto;
      flex-wrap: nowrap;
      padding-bottom: 8px;
      -webkit-overflow-scrolling: touch;
    }
    .lp-filter-btn {
      padding: 6px 14px;
      font-size: 0.85rem;
    }

    .lp-b-grid { gap: 16px; }
    .lp-b-card { padding: 24px 20px; }

    .lp-trust-icons { gap: 32px; }
  }
</style>

<div class="lp-wrapper">

  <!-- HERO -->
  <section class="lp-hero">
    <div class="lp-container">
      <h1>{{ hero_title }}</h1>
      <p>{{ hero_sub }}</p>
    </div>
  </section>

  <!-- COUNTRY LIST -->
  <section class="lp-list-section">
    <div class="lp-container">

      <!-- Continent Filter -->
      <div class="lp-filter-bar" id="lp-filters">
        <button class="lp-filter-btn active" data-continent="all">All</button>
        <button class="lp-filter-btn" data-continent="asia">Asia</button>
        <button class="lp-filter-btn" data-continent="europe">Europe</button>
        <button class="lp-filter-btn" data-continent="north-america">North America</button>
        <button class="lp-filter-btn" data-continent="south-america">South America</button>
        <button class="lp-filter-btn" data-continent="oceania">Oceania</button>
        <button class="lp-filter-btn" data-continent="africa">Africa</button>
      </div>

      <!-- Search -->
      <div class="lp-search-wrap">
        <svg class="lp-search-icon" viewBox="0 0 24 24" fill="none" stroke="currentColor" stroke-width="2">
          <circle cx="11" cy="11" r="8"/><path d="m21 21-4.3-4.3"/>
        </svg>
        <input type="text" id="lp-search" class="lp-search-box" placeholder="Search country name...">
      </div>

      <!-- Country Grid -->
      <div class="lp-flat-grid" id="lp-grid">
        {% for country in localization.available_countries %}
          <div class="lp-item" data-name="{{ country.name | downcase }}" data-code="{{ country.iso_code }}">
            {{ country | image_url: width: 48 | image_tag: loading: 'lazy' }}
            <span>{{ country.name }}</span>
          </div>
        {% endfor %}
      </div>

      <div id="lp-empty" class="lp-empty">No countries found matching your search.</div>

      <!-- Shipping Notes -->
      <div class="lp-notes">
        <h3>Shipping Coverage & Notes</h3>
        <p>Our shipping availability may vary based on local regulations, logistics conditions, and carrier limitations.</p>
        <p>If your country does not appear on the list above, it means we are currently unable to ship there directly. Availability may change over time as logistics conditions evolve.</p>
        <p class="lp-notes-help"><strong>Need help confirming shipping options?</strong><br>Our support team is happy to assist if you have questions about delivery to your location. Please contact us via email at <a href="mailto:support@lavividhair.com">support@lavividhair.com</a></p>
      </div>
    </div>
  </section>

  <!-- BENEFITS -->
  <section class="lp-benefits">
    <div class="lp-container">
      <div class="lp-b-grid">
        <div class="lp-b-card">
          <svg class="lp-b-icon" viewBox="0 0 24 24" fill="none" stroke="currentColor" stroke-width="1.5">
            <path d="M14 18V6a2 2 0 0 0-2-2H4a2 2 0 0 0-2 2v11a1 1 0 0 0 1 1h2"/><path d="M15 18H9"/><path d="M19 18h2a1 1 0 0 0 1-1v-3.65a1 1 0 0 0-.22-.624l-3.48-4.35A1 1 0 0 0 17.52 8H14"/><circle cx="17" cy="18" r="2"/><circle cx="7" cy="18" r="2"/>
          </svg>
          <h3>US Ground</h3>
          <p>{{ us_shipping_time }} Business Days<br><small>Free over {{ us_free_threshold | money }}</small></p>
        </div>
        <div class="lp-b-card">
          <svg class="lp-b-icon" viewBox="0 0 24 24" fill="none" stroke="currentColor" stroke-width="1.5">
            <circle cx="12" cy="12" r="10"/><path d="M2 12h20"/><path d="M12 2a15.3 15.3 0 0 1 4 10 15.3 15.3 0 0 1-4 10 15.3 15.3 0 0 1-4-10 15.3 15.3 0 0 1 4-10z"/>
          </svg>
          <h3>International</h3>
          <p>{{ intl_shipping_time }} Business Days<br><small>Free over {{ intl_free_threshold | money }}</small></p>
        </div>
        <div class="lp-b-card">
          <svg class="lp-b-icon" viewBox="0 0 24 24" fill="none" stroke="currentColor" stroke-width="1.5">
            <path d="M12 22s8-4 8-10V5l-8-3-8 3v7c0 6 8 10 8 10"/><path d="m9 12 2 2 4-4"/>
          </svg>
          <h3>45-Day Guarantee</h3>
          <p>Free returns on first purchase<br><small>Money back, worry-free</small></p>
        </div>
      </div>
    </div>
  </section>

  <!-- RESTRICTIONS -->
  <section class="lp-restrict">
    <div class="lp-container">
      <h2 class="lp-restrict-title">Important Restrictions</h2>
      <div class="lp-restrict-box">
        <h4>Military & P.O. Box Addresses</h4>
        <p>We cannot ship to APO/FPO addresses (including Hawaii, Alaska, Puerto Rico, Guam). Please use a physical street address.</p>

        <h4>Customs & Import Duties</h4>
        <p>International packages may be charged import tariffs. These fees are determined by your country's customs and are the buyer's responsibility.</p>

        <h4>Signature Required</h4>
        <p>DHL international orders and high-value items require signature upon delivery. If waived, LaVivid is not responsible for lost packages.</p>
      </div>
    </div>
  </section>

  <!-- TRUST & CTA -->
  <section class="lp-trust">
    <div class="lp-container">
      <div class="lp-trust-icons">
        <div class="lp-t-item">
          <svg class="lp-t-icon" viewBox="0 0 24 24" fill="none" stroke="currentColor" stroke-width="1.5">
            <path d="m7.5 4.27 9 5.15"/><path d="M21 8a2 2 0 0 0-1-1.73l-7-4a2 2 0 0 0-2 0l-7 4A2 2 0 0 0 3 8v8a2 2 0 0 0 1 1.73l7 4a2 2 0 0 0 2 0l7-4A2 2 0 0 0 21 16Z"/><path d="m3.3 7 8.7 5 8.7-5"/><path d="M12 22V12"/>
          </svg>
          <span>Discreet Packaging</span>
        </div>
        <div class="lp-t-item">
          <svg class="lp-t-icon" viewBox="0 0 24 24" fill="none" stroke="currentColor" stroke-width="1.5">
            <path d="M20 10c0 6-8 12-8 12s-8-6-8-12a8 8 0 0 1 16 0Z"/><circle cx="12" cy="10" r="3"/>
          </svg>
          <span>Fully Tracked</span>
        </div>
        <div class="lp-t-item">
          <svg class="lp-t-icon" viewBox="0 0 24 24" fill="none" stroke="currentColor" stroke-width="1.5">
            <circle cx="12" cy="12" r="10"/><path d="m9 12 2 2 4-4"/>
          </svg>
          <span>Shipping Insurance</span>
        </div>
        <div class="lp-t-item">
          <svg class="lp-t-icon" viewBox="0 0 24 24" fill="none" stroke="currentColor" stroke-width="1.5">
            <path d="M4 14a1 1 0 0 1-.78-1.63l9.9-10.2a.5.5 0 0 1 .86.46l-1.92 6.02A1 1 0 0 0 13 10h7a1 1 0 0 1 .78 1.63l-9.9 10.2a.5.5 0 0 1-.86-.46l1.92-6.02A1 1 0 0 0 11 14z"/>
          </svg>
          <span>Free 1st Reship</span>
        </div>
      </div>
      <h2>Ready for your new look?</h2>
      <a href="/collections/men-hair-system" class="lp-btn">Shop Hair Systems</a>
    </div>
  </section>

</div>

