  // Assign continent data to each item
  items.forEach(item => {
    const code = item.getAttribute('data-code');
    const continent = continentMap[code] || 'other';
    item.setAttribute('data-continent', continent);
  });

  function filterItems() {
    let visibleCount = 0;

    items.forEach(item => {
      const name = item.getAttribute('data-name') || '';
      const continent = item.getAttribute('data-continent') || '';

      const matchesSearch = name.includes(searchTerm);
      const matchesContinent = activeContinent === 'all' || continent === activeContinent;

      if (matchesSearch && matchesContinent) {
        item.classList.remove('lp-hidden');
        visibleCount++;
      } else {
        item.classList.add('lp-hidden');
      }
    });

    emptyMsg.style.display = visibleCount === 0 ? 'block' : 'none';
  }

  // Filter button click
  filterBtns.forEach(btn => {
    btn.addEventListener('click', function() {
      filterBtns.forEach(b => b.classList.remove('active'));
      this.classList.add('active');
      activeContinent = this.getAttribute('data-continent');
      filterItems();
    });
  });

  // Search input with debounce
  let debounceTimer;
  searchInput.addEventListener('input', function(e) {
    clearTimeout(debounceTimer);
    debounceTimer = setTimeout(() => {
      searchTerm = e.target.value.toLowerCase().trim();
      filterItems();
    }, 150);
  });
})();
</script>
